import jxl.*
import groovy.json.JsonSlurper
import java.io.*
import jxl.write.*

def name,value
//Excel with input data
String filepath = "C:/Users/Anoop/Desktop/SDET/Project-work-REST/my-app/UserDetails_TC_004.xls"
	File Excel = new File(filepath)
	Workbook wb = Workbook.getWorkbook(Excel)
	Sheet sh = wb.getSheet("Data")

def rowsize = sh.getRows().toInteger()
def colsize =  sh.getColumns().toInteger()
log.info "No of rows = " + rowsize-1
log.info "No of columns = " +colsize

//Excel with output data
File fexcel = new File("C:/Users/Anoop/Desktop/SDET/Project-work-REST/my-app/Result_TC_004.xls")
        WritableWorkbook writebook = Workbook.createWorkbook(fexcel);
        writebook.createSheet("Result", 0);
        WritableSheet writesheet = writebook.getSheet(0);
        Label dateHeader = new Label(0,0,"Date");
        writesheet.addCell(dateHeader);
        Label countHeader = new Label(1,0,"Count");
        writesheet.addCell(countHeader);


def access_token = context.testCase.getTestStepByName('Properties').getPropertyValue("access_token")
def rowNo = 1
//Run the request for each date
for (i = 7;i<17;i++){
	def date = sh.getCell(1,i).getContents()
	log.info date
	def get = new URL("http://rest-api.upskills.in/api/rest_admin/customers/added_on/"+date).openConnection()
	get.setRequestProperty("Authorization","Bearer "+access_token)
	def RC = get.getResponseCode()
	def response = get.getInputStream().getText()
	if(RC.equals(200)){
		//fetch the response and print it
		log.info response
		log.info "Get Customer list is success" + " for date " + date
	}
	def obj = new JsonSlurper().parseText(response)
	def cust_id = obj.data.customer_id
	def count = cust_id.size().toString()
	log.info cust_id
	log.info "Count for the date = " + count

	// Writing output in excel
	Label dateValue = new Label(0,rowNo,date);
     writesheet.addCell(dateValue);
     Label countValue = new Label(1,rowNo,count);
     writesheet.addCell(countValue);
	rowNo++

}


wb.close()

//Writing in excel

writebook.write();
writebook.close();

