import jxl.*
import groovy.json.JsonSlurper
import java.io.*
import jxl.write.*

def response = testRunner.testCase.getTestStepByName("Get Customer list from a date").getPropertyValue("response")
log.info response
def obj = new JsonSlurper().parseText(response)
def date_added = obj.data.date_added
def total = date_added.size()

def countJan = countFeb = countMar = countApr = countMay = countJun = countJul = 0
for(i = 0; i<total; i++){
	def date = date_added[i]
	def month = date_added[i][3]+date_added[i][4]
	log.info " Date added = " + date + " and month = " + month
	if(month == '01'){
		countJan++
	}else if(month == '02'){
		countFeb++
	}else if(month == '03'){
		countMar++
	}else if(month == '04'){
		countApr++
	}else if(month == '05'){
		countMay++
	}else if(month == '06'){
		countJun++
	}else if(month == '07'){
		countJul++
	}
}


//Writing results in excel

File fexcel = new File("C:/Users/Anoop/Desktop/SDET/Project-work-REST/my-app/Result_TC_005.xls")
        WritableWorkbook writebook = Workbook.createWorkbook(fexcel);
        writebook.createSheet("Result", 0);
        WritableSheet writesheet = writebook.getSheet(0);
        Label dateHeader = new Label(0,0,"Month");
        writesheet.addCell(dateHeader);
        Label countHeader = new Label(1,0,"Count");
        writesheet.addCell(countHeader);

        Label monthValue = new Label(0,1,"January");
        writesheet.addCell(monthValue);
        Label countValue = new Label(1,1,countJan.toString());
        writesheet.addCell(countValue);

        monthValue = new Label(0,2,"February");
        writesheet.addCell(monthValue);
        countValue = new Label(1,2,countFeb.toString());
        writesheet.addCell(countValue);

        monthValue = new Label(0,3,"March");
        writesheet.addCell(monthValue);
        countValue = new Label(1,3,countMar.toString());
        writesheet.addCell(countValue);

        monthValue = new Label(0,4,"April");
        writesheet.addCell(monthValue);
        countValue = new Label(1,4,countApr.toString());
        writesheet.addCell(countValue);

        monthValue = new Label(0,5,"May");
        writesheet.addCell(monthValue);
        countValue = new Label(1,5,countMay.toString());
        writesheet.addCell(countValue);

        monthValue = new Label(0,6,"June");
        writesheet.addCell(monthValue);
        countValue = new Label(1,6,countJun.toString());
        writesheet.addCell(countValue);

        monthValue = new Label(0,7,"July");
        writesheet.addCell(monthValue);
        countValue = new Label(1,7,countJul.toString());
        writesheet.addCell(countValue);


//Writing in excel
writebook.write();              
writebook.close();
